<?php

namespace App\Console\Commands;

use Illuminate\Console\Command;
use Illuminate\Support\Facades\Storage;
use App\Models\Product;

class SetupProductImages extends Command
{
    protected $signature = 'setup:product-images';
    protected $description = 'Setup product images';

    public function handle()
    {
        // Create products directory if it doesn't exist
        if (!Storage::disk('public')->exists('products')) {
            Storage::disk('public')->makeDirectory('products');
        }

        // Sample image data with custom names
        $images = [
            'chocolate-cake' => [
                'data' => '',
                'type' => 'jpg',
                'name' => 'chocolate-cake.jpg'
            ],
            'vanilla-cupcake' => [
                'data' => '',
                'type' => 'jpg',
                'name' => 'vanilla-cupcake.jpg'
            ],
            'croissant' => [
                'data' => '',
                'type' => 'jpg',
                'name' => 'croissant.jpg'
            ],
            'red-velvet-cake' => [
                'data' => '',
                'type' => 'jpg',
                'name' => 'red-velvet-cake.jpg'
            ],
            'chocolate-chip-cookie' => [
                'data' => '',
                'type' => 'jpg',
                'name' => 'chocolate-chip-cookie.jpg'
            ],
            'blueberry-muffin' => [
                'data' => '',
                'type' => 'jpg',
                'name' => 'blueberry-muffin.jpg'
            ],
            'cheesecake' => [
                'data' => '',
                'type' => 'jpg',
                'name' => 'cheesecake.jpg'
            ],
            'almond-croissant' => [
                'data' => '',
                'type' => 'jpg',
                'name' => 'almond-croissant.jpg'
            ],
            // New products
            'tiramisu' => [
                'data' => '',
                'type' => 'jpg',
                'name' => 'tiramisu.jpg'
            ],
            'macaron' => [
                'data' => '',
                'type' => 'jpg',
                'name' => 'macaron.jpg'
            ],
            'eclair' => [
                'data' => '',
                'type' => 'jpg',
                'name' => 'eclair.jpg'
            ],
            'fruit-tart' => [
                'data' => '',
                'type' => 'jpg',
                'name' => 'fruit-tart.jpg'
            ],
            'cinnamon-roll' => [
                'data' => '',
                'type' => 'jpg',
                'name' => 'cinnamon-roll.jpg'
            ],
            'pumpkin-pie' => [
                'data' => '',
                'type' => 'jpg',
                'name' => 'pumpkin-pie.jpg'
            ]
        ];

        // Get all products
        $products = Product::all();

        foreach ($products as $product) {
            $slug = \Str::slug($product->name);
            
            // Find matching image
            foreach ($images as $name => $imageData) {
                if (strpos($slug, $name) !== false) {
                    // Use custom name if provided, otherwise generate one
                    $filename = $imageData['name'] ?? (time() . '_' . $slug . '.' . $imageData['type']);
                    $imageData = base64_decode($imageData['data']);
                    
                    // Save the image
                    $path = 'products/' . $filename;
                    Storage::disk('public')->put($path, $imageData);
                    
                    // Update product
                    $product->image = $filename;
                    $product->save();
                    
                    $this->info("Updated image for {$product->name} at path: {$path}");
                    break;
                }
            }
        }

        $this->info('Product images have been set up successfully!');
    }
} 